package codegym.service;

import codegym.model.Product;
import org.springframework.beans.factory.annotation.Autowired;

import java.util.ArrayList;

public class ProductCRUD implements ICRUD<Product> {
    private ArrayList<Product> products;
 //   @Autowired
    CategoryCRUD categoryCRUD=new CategoryCRUD();
    public ProductCRUD() {
        products = new ArrayList<>();
        products.add(new Product(1, "Asus vivobook", 1500, "data:image/jpeg;base64,", categoryCRUD.getAll().get(0), true));
        products.add(new Product(2, "Dell", 1000, "data:image/jpeg;base64,", categoryCRUD.getAll().get(0), true));
        products.add(new Product(3, "Iphone 13", 1500, "data:image/jpeg;base64,",categoryCRUD.getAll().get(1),true));
    }

    @Override
    public ArrayList<Product> getAll() {
        return products;
    }

    @Override
    public Product findByID(int id) {
        for (Product p:products
             ) {
            if (p.getIdP()==id){
                return p;
            }
        }
        return null;
    }

    @Override
    public Product findByName(String name) {
        return null;
    }

    @Override
    public int getIndex(int id) {
        for (int i = 0; i < products.size(); i++) {
            if (products.get(i).getIdP()==id){
                return i;
            }
        }
        return -1;
    }
    public ArrayList<Product> search(String key){
        ArrayList<Product> products1=new ArrayList<>();
        for (Product p :
                products) {
            if (check(key,p.getpName())){
                products1.add(p);
            }
        }
        return products1;
    }
    public static boolean check(String key,String name){
        name=name.toLowerCase();
        key=key.toLowerCase();
        return name.contains(key);
    }


}
